  external ffi.Pointer<ffi.UnsignedChar> checksum_ptr;

  external ffi.Pointer<
      ffi.NativeFunction<
          ffi.UnsignedLong Function(
              ffi.UnsignedLong checksum,
              ffi.Pointer<ffi.Uint8> buf,
              ffi.UnsignedInt size)>> update_checksum;

